class FacesAutoencoder:

    def __init__(self, latent_space, input_shape):

        self._latent_space = latent_space
        self._input_shape = input_shape

        self._encoder = None
        self._decoder = None
        self._autoencoder = None
        self._configure_network()

    def _configure_network(self):
        encoder_input = inputs = Input(shape=(self._input_shape,))
        encoder_output = Dense(self._latent_space)(encoder_input)

        decoder_input = Dense(256, activation="relu", input_shape=(self._latent_space,) )(encoder_output)
        decoder_output = Dense(input_shape, activation="sigmoid")(decoder_input)

        self._autoencoder = Model(encoder_input, decoder_output)
        self._encoder = Model(encoder_input, encoder_output)
        #print( self._autoencoder.summary())

        self._autoencoder.compile(optimizer="Adam", loss="mse", metrics=["accuracy"])

    def encode(self, X):
        return self._encoder.predict(X)

    def predict(self, X):
        return self._autoencoder.predict(X)

    def fit(self, X, epochs, ):
        for epoch in range(epochs):
            print(' epoch ', epoch)
            losses = []
            random.shuffle(X)
            for element in X:
                #print('element ' , element.shape)
                el=element.reshape(1,2914)
                loss = self._autoencoder.fit(el, el, epochs=1, verbose=0).history['loss'][0]
                losses.append(loss)

    def decode(self, X): #, timesteps):
        return Decoder(self._autoencoder, self._latent_space).predict(X)#, timesteps)

class Decoder:

    def __init__(self, autoencoder, latent_space):

        self._autoencoder = autoencoder
        self._latent_space = latent_space

    def predict(self, X):
        decoder_input = Input(shape=(self._latent_space,))

        decoder_repeated_input = (decoder_input)
        decoder = self._autoencoder.layers[-1](decoder_repeated_input)
        decoder = Model(decoder_input, decoder)

        return decoder.predict(X)
